std::vector<T> mergeSort(const std::vector<T>& xs) {
    if (xs.size() <= 1) {
        return xs;
    }
    int n = xs.size() / 2;
    return merge(mergeSort(std::vector<T>(xs.begin(), xs.begin() + n)),
